mapper.select:
SELECT * FROM KC001


        product_id = #{product_id}


        and repository_id = #{repository_id}


        and freeuse1 = #{freeuse1}


        and freeuse2 = #{freeuse2}


        and freeuse3 = #{freeuse3}

mapper.delete:
DELETE FROM KC001


        product_id = #{product_id}


        and repository_id = #{repository_id}
