mapper.select:
SELECT * FROM CG001


        purchase_note_id = #{purchase_note_id}


        and sales_order_note_id = #{sales_order_note_id}


        and supplier_id = #{supplier_id}


        and repository_id = #{repository_id}


        and operator_id = #{operator_id}


        and depart_id = #{depart_id}


        and entry_date >= #{entry_date_from}


        and entry_date <= #{entry_date_to}


        and pay_date >= #{pay_date_from}


        and pay_date <= #{pay_date_to}
